import React, { useEffect } from "react";
import "./style.css";
import { useState } from "react";
let arrData = [{ firstname: "MK" }, { firstname: "KK" }, { firstname: "Mj" }, { firstname: "RR" }];

const TestData = () => {
    const [nameArr, setNameArr] = useState([]);
    const [selectedNameArr, setSelectedNameArr] = useState([]);

    useEffect(() => {
        if (nameArr) {
            console.log(nameArr);
        }
    }, [nameArr]);

    const handleClickArr = (userName) => {
        setNameArr([...nameArr, { firstname: userName }]);
    };

    const handleNameClick = (userName) => {
        if(!selectedNameArr.includes(userName)){
            setSelectedNameArr([...selectedNameArr,{ firstname: userName }]);
            handleClickArr(userName);
        }

    };

    return (
        <>
            {arrData.map((item, index) => {
                return (
                <>
                    <label
                        onClick={() => handleNameClick(item.firstname)}
                        className={selectedNameArr.includes(item.firstname) ? "user-Name selected" : "user-Name"}
                        key={index}
                    >
                        {item.firstname}
                    </label>{" "}
                </>
                );
            })}
        </>
    );
};

export default TestData;
