import React, { useState } from "react";
import logo from "../../assets/Images/geeker_logo.png";
import BasicButtons from "../../components/Button";
import { Container, Row, Col } from "react-bootstrap";
import Box from "@mui/material/Box";
import { Divider, Link } from "@mui/material";
import { Modal, ToggleButton, ButtonGroup } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import { Icon } from "react-icons-kit";
import { eyeOff } from "react-icons-kit/feather/eyeOff";
import { eye } from "react-icons-kit/feather/eye";

const LoginPage = () => {
  const navigate = useNavigate();
  const [show, setShow] = useState(false);
  const [radioValue, setRadioValue] = useState("customer");
  const [password, setPassword] = useState("");
  const [type, setType] = useState("password");
  const [icon, setIcon] = useState(eyeOff);
  const [userDetails, setUserDetails] = useState({
    email: "",
    password: "",
    validEmail: "",
    validPassword: "",
  });
  const [revalidation, setRevalidation] = useState({
    isEmailValid: false,
    isPasswordValid: false,
  });
  return (
    <Container>
      <Modal show={show} onHide={() => setShow(false)}>
        <Modal.Header>
          <p>Pick Registration Type</p>
        </Modal.Header>
        <Modal.Body className="text-center">
          <ButtonGroup>
            {radios.map((radio, idx) => (
              <ToggleButton
                className="px-5 py-3"
                key={idx}
                id={`radio-${idx}`}
                type="radio"
                // variant="outline-info"
                variant={idx % 2 ? "outline-info" : "outline-info"}
                name="radio"
                value={radio.value}
                checked={radioValue === radio.value}
                onChange={(e) => setRadioValue(e.currentTarget.value)}
              >
                {radio.name}
              </ToggleButton>
            ))}
          </ButtonGroup>
        </Modal.Body>
        <Modal.Footer>
          <BasicButtons
            onClick={() => setShow(false)}
            type={"button"}
            width={"85px"}
            color={"white"}
            height={"30px"}
            fontWeight={"600"}
            loader={false}
            background={"#97abb6"}
            btnName={"Close"}
            disabled={false}
          />
          <BasicButtons
            onClick={register}
            type={"button"}
            width={"85px"}
            color={"white"}
            height={"30px"}
            fontWeight={"600"}
            loader={false}
            background={"rgb(1, 212, 213)"}
            btnName={"Start"}
            disabled={false}
          />
        </Modal.Footer>
      </Modal>
      <div className="login-div-form my-4">
        <img src={logo} />
        <Divider className="my-4">
          <h5>Login</h5>
        </Divider>
        <div className="login-form">

        </div>













        <div className="d-flex flex-wrap flex-row justify-content-center align-items-center gap-2 py-3">
          <span>Need an Account?</span>
          <Link onClick={() => setShow(true)} style={{ cursor: "pointer" }}>
            Register here
          </Link>
        </div>
        <Link
          to="/register"
          state={{ some: "value" }}
          style={{ cursor: "pointer" }}
        >
          Forgot password
        </Link>
      </div>
    </Container>
  )
}
export default LoginPage
