import React, { useEffect } from "react";
import "./style.css";
import { useState } from "react";
let arrData = [{ firstname: "MK" }, { firstname: "KK" }, { firstname: "Mj" }, { firstname: "RR" }];

const TestData = () => {
    const [nameArr, setNameArr] = useState([]);
    const [selectedNames, setSelectedNames] = useState([]);

    useEffect(() => {
        if (nameArr) {
            console.log(nameArr);
        }
    }, [nameArr]);

    const handleNameClick = (userName) => {
        if (!selectedNames.includes(userName) && !nameArr.some(item => item.firstname === userName)) {
            setNameArr([...nameArr, { firstname: userName }]);
            setSelectedNames([...selectedNames, userName]);
        }else{
            setNameArr(nameArr.filter(item => item.firstname !== userName));
            setSelectedNames(selectedNames.filter(name => name !== userName));
        }
    };


    // const handleNameClick = (userName) => {
    //     if (!selectedNames.includes(userName)) {
    //         setSelectedNames([...selectedNames, userName]);
    //     } else {
    //         setSelectedNames(selectedNames.filter((name) => name !== userName));
    //     }
    //     handleClickArr(userName);
    // };

    return (
        <>
            {arrData.map((item, index) => {
                return (
                  <>
                      <label
                        onClick={() => handleNameClick(item.firstname)}
                        className={selectedNames.includes(item.firstname) ? "user-Name selected" : "user-Name"}
                        key={index}
                    >
                        {item.firstname}
                    </label>{" "}
                  </>
                );
            })}
        </>
    );
};

export default TestData;
